import org.mindrot.jbcrypt.BCrypt;

public class PasswordService {

    public String hashPassword(String plainPassword) {
        return BCrypt.hashpw(plainPassword, BCrypt.gensalt(12));
    }

    public boolean verifyPassword(String plainPassword, String hashedPassword) {
        return BCrypt.checkpw(plainPassword, hashedPassword);
    }

    public static void main(String[] args) {
        PasswordService service = new PasswordService();
        String password = "Sicher123!";
        String hash = service.hashPassword(password);
        System.out.println("Hash: " + hash);

        boolean isValid = service.verifyPassword(password, hash);
        System.out.println("Passwort korrekt: " + isValid);
    }
}

class AccessControl {
  constructor() {
    this.roles = {
      admin: ['read', 'write', 'delete', 'manage_users'],
      editor: ['read', 'write'],
      viewer: ['read']
    };
    this.userRoles = new Map();
  }

  assignRole(userId, role) {
    if (!this.roles[role]) {
      throw new Error(`Rolle ${role} existiert nicht`);
    }
    this.userRoles.set(userId, role);
  }

  hasPermission(userId, permission) {
    const role = this.userRoles.get(userId);
    if (!role) return false;
    return this.roles[role].includes(permission);
  }
}

const ac = new AccessControl();
ac.assignRole('user123', 'editor');
console.log(ac.hasPermission('user123', 'read'));   // true
console.log(ac.hasPermission('user123', 'delete')); // false
